mapper.select:
SELECT ORDER_ID, USER_ID, DELIVERY_STATUS, ORDER_STATUS, ORDER_DATE, GOODS_ID, GOODS_PRICE, REVIEW_STATUS, GOODS_NAME, G_ATTACH_ONAME, G_ATTACH_TYPE, G_ATTACH_DATA, IMG_ROLE FROM ( SELECT rownum as rnum, ORDER_ID, USER_ID, DELIVERY_STATUS, ORDER_STATUS, ORDER_DATE, GOODS_ID, GOODS_PRICE, REVIEW_STATUS, GOODS_NAME, G_ATTACH_ONAME, G_ATTACH_TYPE, G_ATTACH_DATA, IMG_ROLE FROM ( SELECT o.ORDER_ID, o.USER_ID, o.DELIVERY_STATUS, o.ORDER_STATUS, o.ORDER_DATE, oi.GOODS_ID, oi.GOODS_PRICE, oi.REVIEW_STATUS, gi.GOODS_NAME, gi.G_ATTACH_ONAME, gi.G_ATTACH_TYPE, gi.G_ATTACH_DATA, gi.IMG_ROLE FROM "ORDER" o JOIN order_item oi ON o.ORDER_ID = oi.ORDER_ID JOIN goods_img gi ON oi.GOODS_ID = gi.GOODS_ID WHERE o.USER_ID = #{userId} AND oi.REVIEW_STATUS = #{reviewStatus}
AND o.ORDER_DATE BETWEEN add_months(SYSDATE, -#{month}) AND SYSDATE
ORDER BY o.ORDER_DATE DESC ) WHERE rownum <= #{endRowNo} ) WHERE rnum >= #{startRowNo}
